mapper.select:
	SELECT COUNT(*)
	FROM TB_BOARD A, TB_MEMBER B
	WHERE A.REG_USER_ID = B.MEMBER_ID
	AND A.USE_YN = 'Y'
		
      	AND A.MENU_ID = #{menu_id}
    	
    	
      	AND A.REG_USER_ID = #{reg_user_id}
    	
    	
      	AND A.REG_DATE = #{reg_date}

SELECT A.BOARD_NO , A.MENU_ID , A.TITLE , A.REG_USER_ID , B.MEMBER_ID AS REG_USER_NM , A.REG_DATE , A.SORT_NO FROM TB_BOARD A, TB_MEMBER B WHERE A.REG_USER_ID = B.MEMBER_ID AND A.USE_YN = 'Y'
AND A.MENU_ID = #{menu_id}
AND A.TITLE = #{title}
AND A.REG_USER_ID = #{user_id}
AND DATE_FORMAT(A.REG_DATE, '%y/%m/%d ') BETWEEN #{reg_date_start} AND #{reg_date_end}
ORDER BY R1.SORT_NO ASC, R1.REG_DATE DESC

mapper.update:
	UPDATE TB_BOARD
	SET MOD_USER_ID = #{mod_user_id}
    , MOD_DATE = now()
    	
    	, TITLE = #{title}
      	
      	
      	, CONTENT = #{content}
      	
    WHERE BOARD_NO = #{board_no}

	UPDATE TB_BOARD 
 SET MOD_USER_ID = #{mod_user_id}
 , MOD_DATE = now()
 , USE_YN = 'N'
 WHERE BOARD_NO = #{board_no}
